/* eslint-env: node */

class Point {
  constructor(x, y) {
    this.x = parseInt(x, 10);
    this.y = parseInt(y, 10);
  }
}

function buildKeypad(rows, cols) {
  let counter = 1;
  const keypad = new Array(rows);
  for (let x = 0; x < rows; x += 1) {
    const row = new Array(cols);
    for (let y = 0; y < cols; y += 1) {
      row[y] = counter;
      counter += 1;
    }
    keypad[x] = row;
  }
  return keypad;
}

const rows = 5;
const cols = 5;

const keypad = [
  [0, 0, 1, 0, 0],
  [0, 2, 3, 4, 0],
  [5, 6, 7, 8, 9],
  [0, 'A', 'B', 'C', 0],
  [0, 0, 'D', 0, 0],
];

const lines = [
  '',
  '',
  '',
  '',
  '',
];
// const lines = [
//   'ULL',
//   'RRDDD',
//   'LURDL',
//   'UUUUD',
// ];

const position = new Point(0, 2);
const sequence = [];

lines.forEach((line) => {
  for (let i = 0; i < line.length; i += 1) {
    const c = line[i];

    if (c === 'U' && position.y > 0 && keypad[position.y - 1][position.x] !== 0) {
      position.y -= 1;
    } else if (c === 'D' && position.y < (cols - 1) && keypad[position.y + 1][position.x] !== 0) {
      position.y += 1;
    } else if (c === 'L' && position.x > 0 && keypad[position.y][position.x - 1] !== 0) {
      position.x -= 1;
    } else if (c === 'R' && position.x < (rows - 1) && keypad[position.y][position.x + 1] !== 0) {
      position.x += 1;
    }
  }
  sequence.push(keypad[position.y][position.x]);
});

console.log(sequence);
